Template.TEMPLATE_NAME.events({

  'click button': function(event, instance) {
    let remaining = 4;
    let timer = null;
    SUIBlock.block(remaining);
    timer = Meteor.setInterval(function () {
      SUIBlock.message.set(--remaining);
      if (remaining < 1) {
        Meteor.clearInterval(timer);
        SUIBlock.unblock();
      }
    }, 1000);
  }

});

